
public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j=1;
		
		
		//for loop
		for( i=1 ; i<=4 ; i++)
		{
			System.out.println("Hello");
	
		}
		
		for( j=8 ; j>=4 ; j--)
		{
			System.out.println("Hi"+ j);
	
		}
		/*
		for( i=8 ; i+j>4 ; i--)
		{
			System.out.println("Good");
	
		}*/
		System.out.println("Hi");
	}

}
